import React from "react";
import { Link } from "react-router-dom";

import { ListItemIcon } from "@mui/material";
const Menu = ({ item }) => {
  return (
    <div style={{ width: "100%", background: "red", height: "auto" }}>
      <ul>
        {item.map((category) => {
          return (
            <span key={category} style={{ background: "red" }}>
              <Link
                to={`/products/category/${category}`}
                // style={{ paddingBottom: "25px", paddingTop: "10px" }}
              >
                <span style={{ fontSize: "1.2rem", color: "white" }}>
                  {category}
                </span>
              </Link>{" "}
              &emsp;
            </span>
          );
        })}
      </ul>
    </div>
  );
};
export default Menu;
