import React from "react";
import { Button, Link } from "@mui/material";
import ButtonGroup from "@mui/material/ButtonGroup";
import Box from "@mui/material/Box";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import { Group } from "@material-ui/icons";
import { ListGroup } from "react-bootstrap";

const Menu = ({ item }) => {
  function titleCase(string) {
    //1. Tách các từ, cụm từ trong chuỗi ban đầu
    let sentence = string.toLowerCase().split(" ");

    //2. Tạo vòng lặp và viết hoa chữ cái đầu tiên của các từ, cụm từ trên
    for (var i = 0; i < sentence.length; i++) {
      sentence[i] = sentence[i][0].toUpperCase() + sentence[i].slice(1);
    }
    //3. Nối các từ, cụm từ đã xử lý ở trên và trả về kết quả
    return sentence.join(" ");
  }

  return (
    <div
      style={{
        width: "100%",
        background: "red",
        height: "2.3rem",
        // marginBottom: "20px",
      }}
    >
      <ul
        style={{
          paddingLeft: "0px",
          fontFamily: "sans-serif",
          listStyleType: "none",
          overflow: "hidden",
        }}
      >
        {item.map((category) => {
          return (
            // <ButtonGroup
            //   variant="text"
            //   aria-label="text button group"
            //   href={`/products/category/${category}`}
            //   key={category}
            //   style={{ background: "red" }}
            // >
            //   <Link
            //     href={`/products/category/${category}`}
            //     underline="hover"
            //     style={{
            //       padding: "10px",
            //       border: "1px solid gray",
            //       borderRadius: "0px",
            //     }}
            //   >
            //     <span style={{ fontSize: "1rem", color: "white" }}>
            //       {category}
            //     </span>
            //   </Link>
            //   &emsp;
            // </ButtonGroup>
            // <ul
            //   style={{
            //     paddingLeft: "0px",
            //     fontFamily: "sans-serif",
            //     listStyleType: "none",
            //     overflow: "hidden",
            //   }}
            // >
            <li
              style={{
                float: "left",
                width: "auto",
                height: "2.3rem",
                lineHeight: " 40px ",
                textAlign: "center",
                borderRight: "1px solid black",
              }}
            >
              <a
                href={`/products/category/${category}`}
                style={{
                  textDecoration: "none",
                  display: "block",
                  width: "100%",
                  height: "100%",
                }}
                hover={{ background: "#F1F1F1", color: "#333" }}
              >
                &ensp;
                {category}
                &ensp;
              </a>
            </li>
            // </ul>
          );
        })}
      </ul>
    </div>
  );
};
export default Menu;
