import {
  ADD_TO_CART,
  UPDATE_CART,
  DELETE_ITEM_CART,
  CHANGE_CARD,
} from "./action-types";
import { getProduct } from "../../servicers/Api";

export const addCart = (id, title, image, price) => {
  return {
    type: ADD_TO_CART,
    payload: {
      id,
      title,
      image,
      price,
      qty: 1,
    },
  };
};

export const updateCart = (id, qty) => {
  return {
    type: UPDATE_CART,
    payload: {
      id,
      qty,
    },
  };
};

export const changeCard = (id, qty) => {
  console.log("gô");
  return {
    type: CHANGE_CARD,
    payload: {
      id,
      qty,
    },
  };
};

export const deleteCart = (id) => {
  console.log("121321321");
  return {
    type: DELETE_ITEM_CART,
    payload: {
      id,
    },
  };
};

const API = () => {
  return getProduct(1);
};

export const testThunk = () => {
  return function (dispatch) {
    return API().then(
      (res) => {
        console.log(res);
        dispatch(deleteCart());
      },
      (error) => dispatch(changeCard())
    );
  };
};
