import React from "react";
import { useDispatch } from "react-redux";
import Box from "@mui/material/Box";
import Grid from "@mui/material/Grid";
import Rating from "@mui/material/Rating";
import Button from "@mui/material/Button";

import { ADD_TO_CART } from "../share/constants/action-types";
import { getProduct } from "../servicers/Api";

import Slider from "../share/components/layout/slider";

const CartPage = (props) => {
  const history = props.history;
  const dispatch = useDispatch();
  const id = props.match.params.id;
  const [product, setProduct] = React.useState(null);
  React.useEffect(() => {
    getProduct(id, {}).then((res) => {
      setProduct(res.data);
      // console.log(res.data);
    });
  }, [id]);

  const addToCart = (type) => {
    if (product) {
      const { id, title, image, price } = product;
      dispatch({
        type: ADD_TO_CART,
        payload: {
          id,
          title,
          image,
          price,
          qty: 1,
        },
      });
    }

    if (type === "buy-now") {
      history.push("/cart");
    }
  };

  return (
    <>
      <Slider />
      <Box
        sx={{ flexGrow: 1 }}
        style={{ background: "rgb(236, 241, 249)", marginTop: "0px" }}
      >
        <Grid style={{ paddingTop: "0px" }} container spacing={2}>
          <Grid item xs={6}>
            <span>
              <Rating
                name="disabled"
                value={product?.rating.rate}
                readOnly
                style={{ padding: "10px" }}
              />
            </span>
            <img
              src={product?.image}
              alt=""
              style={{ width: "100%", height: "400px" }}
            />
          </Grid>
          <Grid item xs={6} style={{ paddingTop: "30px" }}>
            <h1 style={{ color: "red" }}>{product?.title}</h1>
            <h3 style={{ color: "blue" }}>Loại: {product?.category}</h3>
            <ul>
              <li>
                <span>Bảo hành:</span> 12 Tháng
              </li>
              <li>
                <span>Số lượng:</span> {product?.rating.count}
              </li>
              <li id="price">Price: {product?.price}đ</li>
            </ul>

            <div id="add-cart">
              <Button
                variant="contained"
                onClick={() => addToCart("buy-now")}
                className="btn btn-warning mr-2"
              >
                Mua ngay
              </Button>

              <Button
                style={{ background: "orange", marginTop: "20px" }}
                onClick={addToCart}
                className="btn btn-info"
                variant="contained"
              >
                Thêm vào giỏ hàng
              </Button>
            </div>
          </Grid>
        </Grid>
        <div id="product-body" className="row">
          <div className="col-lg-12 col-md-12 col-sm-12">
            <h3>Đánh giá về {product?.title}</h3>
            {product?.description}
          </div>
        </div>
      </Box>
    </>
  );
};

export default CartPage;
