import React from "react";
import { getCategory, getProductsCategory } from "../servicers/Api";
import ProductItem from "../share/components/product-item";

// import Pagination from "../../shared/components/Pagination";

const CategoryPage = (props) => {
  const id = props.match.params.id;
  // console.log(id);
  const [category, setCategory] = React.useState([]);
  const [products, setProducts] = React.useState([]);
  const [totalProduct, setTotalProduct] = React.useState(0);

  React.useEffect(() => {
    // Get Category
    getCategory(id).then((res) => {
      // console.log(res.data);
      setCategory(res.data.data.category);
    });
    // Get Products By Category ID
    getProductsCategory(id).then((res) => {
      console.log(res.data);
      setProducts(res.data.data);

      // Get Total Product
      // setTotalProduct(res.data.data.length);
    });
  }, [id]);

  return (
    <>
      {/*	List Product	*/}
      <div className="products">
        <h3>
          {category} (hiện có {totalProduct} sản phẩm)
        </h3>
        <div className="product-list card-deck">
          {/* {products.map((product) => {
            return <ProductItem item={product} />;
          })} */}
        </div>
      </div>
      {/*	End List Product	*/}
      <div id="pagination">{/* <Pagination pages={pages} /> */}</div>
    </>
  );
};
export default CategoryPage;
