import React from "react";
import { useDispatch } from "react-redux";
import { ADD_TO_CART } from "../share/constants/action-types";
import { getProduct } from "../servicers/Api";

const CartPage = (props) => {
  const history = props.history;
  const dispatch = useDispatch();
  const id = props.match.params.id;
  const [product, setProduct] = React.useState(null);
  React.useEffect(() => {
    getProduct(id, {}).then((res) => {
      setProduct(res.data);
      // console.log(res.data);
    });
  }, [id]);

  const addToCart = (type) => {
    if (product) {
      const { _id, name, image, price } = product;
      dispatch({
        type: ADD_TO_CART,
        payload: {
          _id,
          name,
          image,
          price,
          qty: 1,
        },
      });
    }

    if (type === "buy-now") {
      history.push("/cart");
    }
  };

  return (
    <>
      <div style={{ paddingTop: "15px" }}>
        <div>
          <img
            src={product?.image}
            alt=""
            style={{ width: "200px", height: "250px" }}
          />
        </div>
        <div id="product-details" className="col-lg-6 col-md-6 col-sm-12">
          <h1>{product?.title}</h1>
          <span>{product?.rating.rate}</span>
          <ul>
            <li>
              <span>Bảo hành:</span> 12 Tháng
            </li>
            <li>
              <span>Số lượng:</span> {product?.rating.count}
            </li>
            <li id="price">Giá Bán (chưa bao gồm VAT): {product?.price}đ</li>
          </ul>

          <div id="add-cart">
            <button
              onClick={() => addToCart("buy-now")}
              className="btn btn-warning mr-2"
            >
              Mua ngay
            </button>

            <button onClick={addToCart} className="btn btn-info">
              Thêm vào giỏ hàng
            </button>
          </div>
        </div>
      </div>
      <div id="product-body" className="row">
        <div className="col-lg-12 col-md-12 col-sm-12">
          <h3>Đánh giá về {product?.title}</h3>
          {product?.description}
        </div>
      </div>
    </>
  );
};

export default CartPage;
