import * as React from "react";
import { useTheme } from "@mui/materical/styles";
import Box from "@mui/material/Box";
import MobileStepper from "@mui/material/MobileStepper";
import Paper from "@mui/material/Paper";
import Typography from "@mui/material/Typography";
import Button from "@mui/material/Button";
import KeyboardArrowLeft from "@mui/icons-material/KeyboardArrowLeft";
import KeyboardArrowRight from "@mui/icons-material/KeyboardArrowRight";
import SwipeableViews from "react-swipeable-views";
import { autoPlay } from "react-swipeable-views-utils";

const AutoPlaySwipeableViews = autoPlay(SwipeableViews);

const images = [
  {
    label: "San Francisco – Oakland Bay Bridge, United States",
    imgPath:
      "https://images.unsplash.com/photo-1537944434965-cf4679d1a598?auto=format&fit=crop&w=400&h=250&q=60",
  },
  {
    label: "Bird",
    imgPath:
      "https://images.unsplash.com/photo-1538032746644-0212e812a9e7?auto=format&fit=crop&w=400&h=250&q=60",
  },
  {
    label: "Bali, Indonesia",
    imgPath:
      "https://images.unsplash.com/photo-1537996194471-e657df975ab4?auto=format&fit=crop&w=400&h=250&q=80",
  },
  {
    label: "Goč, Serbia",
    imgPath:
      "https://images.unsplash.com/photo-1512341689857-198e7e2f3ca8?auto=format&fit=crop&w=400&h=250&q=60",
  },
];

const Slider = () => {
  const theme = useTheme();
  const [activeStep, setActiveStep] = React.useState(0);
  const maxSteps = images.length;

  const handleNext = () => {
    setActiveStep((prevActiveStep) => prevActiveStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  const handleStepChange = (step) => {
    setActiveStep(step);
  };
  return (
    // <div id="slide" className="carousel slide" data-ride="carousel">
    //   {/* Indicators */}
    //   <ul className="carousel-indicators">
    //     <li data-target="#slide" data-slide-to={0} className="active" />
    //     <li data-target="#slide" data-slide-to={1} />
    //     <li data-target="#slide" data-slide-to={2} />
    //     <li data-target="#slide" data-slide-to={3} />
    //     <li data-target="#slide" data-slide-to={4} />
    //     <li data-target="#slide" data-slide-to={5} />
    //   </ul>
    //   {/* The slideshow */}
    //   <div className="carousel-inner">
    //     <div className="carousel-item active">
    //       <img src="images/slide-1.png" alt="Vietpro Academy" />
    //     </div>
    //     <div className="carousel-item">
    //       <img src="images/slide-2.png" alt="Vietpro Academy" />
    //     </div>
    //     <div className="carousel-item">
    //       <img src="images/slide-3.png" alt="Vietpro Academy" />
    //     </div>
    //     <div className="carousel-item">
    //       <img src="images/slide-4.png" alt="Vietpro Academy" />
    //     </div>
    //     <div className="carousel-item">
    //       <img src="images/slide-5.png" alt="Vietpro Academy" />
    //     </div>
    //     <div className="carousel-item">
    //       <img src="images/slide-6.png" alt="Vietpro Academy" />
    //     </div>
    //   </div>
    //   {/* Left and right controls */}
    //   <a className="carousel-control-prev" href="#slide" data-slide="prev">
    //     <span className="carousel-control-prev-icon" />
    //   </a>
    //   <a className="carousel-control-next" href="#slide" data-slide="next">
    //     <span className="carousel-control-next-icon" />
    //   </a>
    // </div>

    <Box sx={{ maxWidth: 400, flexGrow: 1 }}>
      <Paper
        square
        elevation={0}
        sx={{
          display: "flex",
          alignItems: "center",
          height: 50,
          pl: 2,
          bgcolor: "background.default",
        }}
      >
        <Typography>{images[activeStep].label}</Typography>
      </Paper>
      <AutoPlaySwipeableViews
        axis={theme.direction === "rtl" ? "x-reverse" : "x"}
        index={activeStep}
        onChangeIndex={handleStepChange}
        enableMouseEvents
      >
        {images.map((step, index) => (
          <div key={step.label}>
            {Math.abs(activeStep - index) <= 2 ? (
              <Box
                component="img"
                sx={{
                  height: 255,
                  display: "block",
                  maxWidth: 400,
                  overflow: "hidden",
                  width: "100%",
                }}
                src={step.imgPath}
                alt={step.label}
              />
            ) : null}
          </div>
        ))}
      </AutoPlaySwipeableViews>
      <MobileStepper
        steps={maxSteps}
        position="static"
        activeStep={activeStep}
        nextButton={
          <Button
            size="small"
            onClick={handleNext}
            disabled={activeStep === maxSteps - 1}
          >
            Next
            {theme.direction === "rtl" ? (
              <KeyboardArrowLeft />
            ) : (
              <KeyboardArrowRight />
            )}
          </Button>
        }
        backButton={
          <Button size="small" onClick={handleBack} disabled={activeStep === 0}>
            {theme.direction === "rtl" ? (
              <KeyboardArrowRight />
            ) : (
              <KeyboardArrowLeft />
            )}
            Back
          </Button>
        }
      />
    </Box>
  );
};
export default Slider;
