import {
  ADD_TO_CART,
  UPDATE_CART,
  DELETE_ITEM_CART,
  INCREASE,
  DECREASE,
} from "./action-types";

export const addCart = (id, title, image, price) => {
  return {
    type: ADD_TO_CART,
    payload: {
      id,
      title,
      image,
      price,
      qty: 1,
    },
  };
};

export const updateCart = (id, qty) => {
  return {
    type: UPDATE_CART,
    payload: {
      id,
      qty,
    },
  };
};

export const increase = (id, qty) => {
  return {
    type: INCREASE,
    payload: {
      id,
      qty,
    },
  };
};

export const decrease = (id, qty) => {
  return {
    type: DECREASE,
    payload: {
      id,
      qty,
    },
  };
};

export const newCart = (id) => {
  return {
    type: DELETE_ITEM_CART,
    payload: {
      id,
    },
  };
};
