import React from "react";
import { Link } from "react-router-dom";
import { BASE_URL } from "../constants/app-api";

const ProductItem = ({ item }) => {
  const getImageProduct = (imageName) => {
    return `${BASE_URL}/img/${imageName}`;
  };
  return (
    <div className="product-item card text-center">
      <Link to={`/product-details-${item._id}`}>
        <img src={getImageProduct(item.image)} alt="" />
      </Link>
      console.log(getImageProduct(item.image))
      <h4>
        <Link href={`/product-details-${item._id}`}>{item.title}</Link>
      </h4>
      <p>
        Giá Bán: <span>{item.price}đ</span>
      </p>
    </div>
  );
};

export default ProductItem;
