import React from "react";
import ProductItem from "../share/components/product-item";
import { getProducts } from "../servicers/Api";
import Pagination from "../share/components/Pagination";

const SearchPage = (props) => {
  const query = new URLSearchParams(props.location.search);
  const q = query.get("q");
  const page = query.get("page") || 1;

  const [pages, updatePages] = React.useState({
    total: 0,
    limit: 6,
    currentPage: page,
  });

  const [products, updateProducts] = React.useState([]);

  React.useEffect(() => {
    getProducts({
      params: {
        name: q,
        limit: 6,
        page: page,
      },
    }).then(({ data }) => {
      updateProducts(data);
      updatePages({ ...pages, ...pages });
    });
  }, [q, page, pages]);

  return (
    <>
      {/*	List Product	*/}
      <div className="products">
        <div id="search-result">
          Kết quả tìm kiếm với từ khóa <span>{q}</span>
        </div>
        <div className="product-list card-deck">
          {products.map((product) => {
            return <ProductItem key={product._id} item={product} />;
          })}
        </div>
      </div>
      {/*	End List Product	*/}
      <div id="pagination">
        <Pagination pages={pages} />
      </div>
    </>
  );
};
export default SearchPage;
