import {
  ADD_TO_CART,
  UPDATE_CART,
  DELETE_ITEM_CART,
  CHANGE_CARD,
} from "./action-types";
import { getProducts } from "../../servicers/Api";

export const addCart = (id, title, image, price) => {
  return {
    type: ADD_TO_CART,
    payload: {
      id,
      title,
      image,
      price,
      qty: 1,
    },
  };
};

export const updateCart = (id, qty) => {
  return {
    type: UPDATE_CART,
    payload: {
      id,
      qty,
    },
  };
};

export const changeCard = (id, qty) => {
  return {
    type: CHANGE_CARD,
    payload: {
      id,
      qty,
    },
  };
};

export const deleteCart = (id) => {
  return {
    type: DELETE_ITEM_CART,
    payload: {
      id,
    },
  };
};

const API = (id) => {
  return fetch(getProducts());
};

export const testThunk = () => {
  return function (dispatch) {
    return API().then(
      (sauce) => dispatch(console.log("12321312")),
      (error) => dispatch(console.log("fruit"))
    );
  };
};
