import React from "react";

const Menu = ({ item }) => {
  return (
    <div
      style={{
        width: "100%",
        background: "red",
        height: "2.3rem",
        // marginBottom: "20px",
      }}
    >
      <ul
        id="menu"
        style={{
          paddingLeft: "0px",
          fontFamily: "sans-serif",
          listStyleType: "none",
          overflow: "hidden",
        }}
      >
        {item.map((category) => {
          return (
            <li
              style={{
                float: "left",
                width: "auto",
                height: "2.3rem",
                lineHeight: " 40px ",
                textAlign: "center",
                borderRight: "1px solid black",
              }}
            >
              <a
                href={`/products/category/${category}`}
                style={{
                  textDecoration: "none",
                  display: "block",
                  width: "100%",
                  height: "100%",
                }}
              >
                <b>&ensp;{category} &ensp;</b>
              </a>
            </li>
            // </ul>
          );
        })}
      </ul>
    </div>
  );
};
export default Menu;
