import React from "react";
// import Pagination from '@mui/material/Pagination';
// import Link from "@mui/material/Link";
import { useLocation, Link } from "react-router-dom";

const Pagination = ({ pages }) => {
  const { pathname, search } = useLocation();
  const query = new URLSearchParams(search);
  // console.log(query);

  const { limit, currentPage, prev, next } = pages;
  const totalPages = Math.ceil(100 / limit);

  function formatUrl(page) {
    query.set("page", page);
    return `${pathname}?${query.toString()}`;
  }

  function renderPagesHTML(delta = 2) {
    const pagesHtml = [];
    const left = currentPage - delta;
    const right = currentPage + delta;
    for (let i = 1; i <= totalPages; i++) {
      if (
        i === 1 ||
        i === currentPage ||
        i === totalPages ||
        (i >= left && i <= right)
      ) {
        pagesHtml.push(i);
      }
    }
    return pagesHtml;
  }

  return (
    <div>
      <ul className="pagination" style={{ float: "right", marginTop: "20px" }}>
        {currentPage > 1 ? (
          <li
            className="page-item"
            style={{ background: "#EB1F27", color: "#FFF", width: "auto" }}
          >
            <a href={formatUrl(prev)} className="page-link" href="#">
              Trang trước
            </a>
          </li>
        ) : null}

        {renderPagesHTML().map((item) => {
          return (
            <li
              className={`page-item ${currentPage === item && "active"}`}
              style={{ background: "#EB1F27", color: "#FFF", width: "auto" }}
            >
              <a href={formatUrl(item)} className={`page-link `} href="#">
                {item}
              </a>
            </li>
          );
        })}

        {currentPage < totalPages ? (
          <li
            className="page-item"
            style={{ background: "#EB1F27", color: "#FFF", width: "auto" }}
          >
            <a href={formatUrl(next)} className="page-link" href="#">
              Trang sau
            </a>
          </li>
        ) : null}
      </ul>
    </div>
  );
};
export default Pagination;
